export default {
  props: {
    type: {
      type: String
    }
  },
  computed: {
    typeClasses () {
      switch (this.type) {
        case 'danger':
          return 'bg-red'

        case 'coloured':
          return 'coloured'

        case 'transparent':
          return 'transparent'

        default:
          return 'coloured'
      }
    }
  }
}
